import React, { useEffect, useState } from 'react';

const SheetEvents = () => {
    const [events, setEvents] = useState([]);
    const [error, setError] = useState(null);
    const [loading, setLoading] = useState(true);

    const SPREADSHEET_ID = '142ofxNSx4iqQfNmyWqQndI8U5fuIVkI0rmwO4c69xB8';
    const API_KEY = 'AIzaSyAWhQF4XMLansrLQRNk3Cx0kwQALO2Rnf8';
    const RANGE = 'Sheet1!A2:D';

    const SHEET_URL = `https://sheets.googleapis.com/v4/spreadsheets/${SPREADSHEET_ID}/values/${RANGE}?key=${API_KEY}`;

    useEffect(() => {
        setLoading(true);
        fetch(SHEET_URL)
            .then((res) => {
                if (!res.ok) {
                    throw new Error('Network response was not ok');
                }
                return res.json();
            })
            .then((data) => {
                const rows = data.values || [];
                const formatted = rows.map((row) => ({
                    name: row[0] || 'Unnamed',
                    date: row[1] || 'No date',
                    time: row[2] || 'No time',
                    description: row[3] || 'No description',
                }));
                setEvents(formatted);
                setError(null);
            })
            .catch((err) => {
                console.error('Error fetching data:', err);
                setError('Failed to load events. Please try again later.');
                setEvents([]);
            })
            .finally(() => {
                setLoading(false);
            });
    }, []);

    return (
        <div>
            <h2>ðŸ“‹ Google Sheet Events</h2>
            {loading && <p>Loading events...</p>}
            {error && <p style={{ color: 'red' }}>{error}</p>}
            {!loading && !error && events.length === 0 && (
                <p>No events found.</p>
            )}
            {events && events.map((event, index) => (
                <div key={index} style={{ marginBottom: '10px' }}>
                    <strong>{event.name}</strong> â€” {event.date} at {event.time}
                    <br />
                    <em>{event.description}</em>
                </div>
            ))}
        </div>
    );
};

export default SheetEvents;
