import React, { useEffect, useState } from 'react';
import { gapi } from 'gapi-script';

const CLIENT_ID = '1025583778510-nhiumsp7ohboveaj8lcbqmstfopup17q.apps.googleusercontent.com';
const API_KEY = 'AIzaSyAWhQF4XMLansrLQRNk3Cx0kwQALO2Rnf8';
const SCOPES = 'https://www.googleapis.com/auth/calendar.events';

const GoogleCalendar = () => {
    const [signedIn, setSignedIn] = useState(false);
    const [events, setEvents] = useState([]);
    const [form, setForm] = useState({
        title: '',
        date: '',
        time: '',
        description: '',
    });
    const [error, setError] = useState('');

    useEffect(() => {
        function start() {
            gapi.client.init({
                apiKey: API_KEY,
                clientId: CLIENT_ID,
                scope: SCOPES,
            }).then(() => {
                gapi.auth2.getAuthInstance().isSignedIn.listen(setSignedIn);
                setSignedIn(gapi.auth2.getAuthInstance().isSignedIn.get());
                if (gapi.auth2.getAuthInstance().isSignedIn.get()) {
                    listEvents();
                }
            });
        }

        gapi.load('client:auth2', start);
    }, []);

    const signIn = () => gapi.auth2.getAuthInstance().signIn();
    const signOut = () => gapi.auth2.getAuthInstance().signOut();

    const listEvents = async () => {
        const response = await gapi.client.calendar.events.list({
            calendarId: 'primary',
            timeMin: new Date().toISOString(),
            singleEvents: true,
            orderBy: 'startTime',
        });
        setEvents(response.result.items);
    };

    const createEvent = async (e) => {
        e.preventDefault();
        setError('');
        
        try {
            // Validate time format
            if (!form.time.match(/^\d{2}:\d{2}$/)) {
                setError('Please enter time in HH:MM format (e.g., 14:30)');
                return;
            }
            
            // Make sure date is valid
            if (!form.date) {
                setError('Please enter a valid date');
                return;
            }
            
            // Create a valid date object
            const startDateTime = new Date(`${form.date}T${form.time}:00`);
            
            // Check if date is valid
            if (isNaN(startDateTime.getTime())) {
                setError('Invalid date or time format');
                return;
            }
            
            // Create end time (1 hour after start time by default)
            const endDateTime = new Date(startDateTime);
            endDateTime.setHours(endDateTime.getHours() + 1);
            
            const event = {
                summary: form.title,
                description: form.description,
                start: {
                    dateTime: startDateTime.toISOString(),
                    timeZone: 'Asia/Karachi',
                },
                end: {
                    dateTime: endDateTime.toISOString(),
                    timeZone: 'Asia/Karachi',
                },
            };

            await gapi.client.calendar.events.insert({
                calendarId: 'primary',
                resource: event,
            });

            listEvents();
            alert('‚úÖ Event created!');
            
            // Clear form
            setForm({
                title: '',
                date: '',
                time: '',
                description: '',
            });
        } catch (err) {
            console.error('Error creating event:', err);
            setError(`Failed to create event: ${err.message}`);
        }
    };

    return (
        <div>
            <h2>üìÖ Google Calendar</h2>
            {!signedIn ? (
                <button onClick={signIn}>üîê Sign in with Google</button>
            ) : (
                <>
                    <button onClick={signOut}>üö™ Sign Out</button>

                    <form onSubmit={createEvent}>
                        <h3>Create Event</h3>
                        {error && <p style={{ color: 'red' }}>{error}</p>}
                        <input
                            placeholder="title"
                            value={form.title}
                            onChange={(e) => setForm({ ...form, title: e.target.value })}
                            required
                        />
                        <input
                            placeholder="date"
                            type="date"
                            value={form.date}
                            onChange={(e) => setForm({ ...form, date: e.target.value })}
                            required
                        />
                        <input
                            placeholder="time"
                            type="time"
                            value={form.time}
                            onChange={(e) => setForm({ ...form, time: e.target.value })}
                            required
                        />
                        <input
                            placeholder="description"
                            value={form.description}
                            onChange={(e) => setForm({ ...form, description: e.target.value })}
                            required
                        />
                        <button type="submit">‚ûï Add Event</button>
                    </form>

                    <h4>Upcoming Events:</h4>
                    <ul>
                        {events.map((e) => (
                            <li key={e.id}>
                                <strong>{e.summary}</strong> ‚Äî {e.start?.dateTime?.split('T')[0]}
                                <br />
                                <em>{e.description}</em>
                            </li>
                        ))}
                    </ul>
                </>
            )}
        </div>
    );
};

export default GoogleCalendar;
